import React, { useEffect, useState } from "react";
import s from "./PeakDetails.module.css";
import { useParams } from "react-router-dom";
import { getPeakData } from "../../services";
import ErrorPage from "../ErrorPage/ErrorPage";

const PeakDetails = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(false);
    const [peakData, setPeakData] = useState();
    let peakId = useParams();


    useEffect(() => {
        setLoading(true);
        const { id } = peakId;
        getPeakData(id)
            .then((res) => {
                setPeakData(res);
                setLoading(false);
            })
            .catch(() => {
                setLoading(false);
                setError(true);
            })
    }, []);

    return (
        <div className={s.detailsBody}>
            {error && <ErrorPage />}
            {loading && <div>Loading...</div>}
            {
                peakData && 
                <div 
                    className={s.peakDetails}

                >
                    <h3>{peakData.name}</h3>
                    <div 
                        className={s.imgContainer}
                        style={{ backgroundImage: `url(${peakData.photo})`}}
                    >
                    </div>
                    <div
                        className={s.info}
                    >
                        <h4>Rank: {peakData.rank}</h4>                        
                        <h4>Elevation: {peakData.elevation}</h4>                        
                        <h4>Range: {peakData.range}</h4>                        
                    </div>
                </div>
            }
        </div>
    )
}

export default PeakDetails;
