export const emailValidation = (str) => {
    const emailRegEx = /[^@ \t\r\n]+@[^@ \t\r\n]+\.[^@ \t\r\n]+/;
    if (str.match(emailRegEx)) {
        console.log(`good to go ${str}`);
        return true;
    }
    return false;
}
