import React, { useContext, useState } from "react";
import s from "./UserRegister.module.css";
import {
    Link,
    useNavigate,
} from "react-router-dom";
import { UserContext } from "../../App";
import { emailValidation } from "../../utilites/emailValidation";


const UserRegister = () => {
    const { authService, updateAuth } = useContext(UserContext);
    const navigate = useNavigate();

    const INIT_STATE = {
        name: '',
        email: '',
        password: '',
    };
    const [error, setError] = useState(false);
    const [errorMsg, setErrorMsg] = useState('');
    const [userInfo, setUserInfo] = useState(INIT_STATE);

    const onChange = ({ target: { name, value }}) => {
        if (name === 'email' && emailValidation(value)) {
                setUserInfo({ ...userInfo, [name]: value });
        }
        setUserInfo({ ...userInfo, [name]: value });
    };

    const createUser = (e) => {
        e.preventDefault();
        const { name, email, password } = userInfo;
        if (!name || !email || !password) {
            Object.keys(userInfo).forEach((key) => {
                console.log(`${key}: ${userInfo[key]}`);
            });
            // Object.keys(userInfo).map((key) => {
            //     if (userInfo[key].length === 0) {
            //         let errorMsg = `Please enter a valid ${key}`;
            //         setError(true);
            //         setErrorMsg(errorMsg);
            //     };
            // });
        } else {
            authService.createUser(name, email, password)
            .then(() => {
                updateAuth();
                navigate("/");
            })
            .catch(() => {
                setError(true);
                setUserInfo({
                    name: '',
                    email: '',
                    password: '',
                });
            });
        };
    };

    return (
        <>
        <form 
            className={s.registerBody}
            onSubmit={createUser}
        >
            <h3>Create Account</h3>
            <p>Enter your email and password</p>
            {error && <div>{errorMsg}</div>}
                <input 
                        className={s.inputBase} 
                        name="name" 
                        type="text" 
                        placeholder="name"
                        autoComplete="off"
                        onChange={onChange}
                />
                <input 
                    className={s.inputBase} 
                    name="email" 
                    type="text" 
                    placeholder="Email"
                    autoComplete="off"
                    onChange={onChange}
                />
                <input 
                    className={s.inputBase} 
                    name="password" 
                    type="password" 
                    placeholder="Password"
                    autoComplete="off"
                    onChange={onChange}
                />
            <input 
                className={s.submitBtn} 
                type="submit" 
                value="Login" 
            />
        </form>
        <div className={s.linkContainer}>
            Already have an account?
            <div className={s.links}>
                <Link to='/login'>Login</Link> 
                <div className={s.lineContainer}> 
                    <hr className={s.line} /> <span>OR</span> <hr className={s.line} />
                </div>
                <Link to='/'>Return Home</Link>
            </div>
        </div>
    </>
    );
};

export default UserRegister;
