import React, { useContext, useState } from "react";
import s from "./LoginUser.module.css";
import { 
    Link, 
    // useNavigate,
    // useLocation,
 } from "react-router-dom";
//  import { AuthService } from "../../services";
import { UserContext } from "../../App";



//  export async function action({ request, params }) {

//     const formData = await request.formData();
//     const user = Object.fromEntries(formData);

//     // console.log('user details', user);
//  }

const LoginUser = () => {
    // const location = useLocation();
    // const navigate = useNavigate();
    const { authService } = useContext(UserContext);
    const [userLogins, setUserLogins] = useState({ email: '', password: '' });
    // const [error, setError] = useState(false);

    const onChange = ({ target: { name, value }}) => {
        setUserLogins({ ...userLogins, [name]: value });
    }

    const loginUser = (e) => {
        e.preventDefault();
        // const formData = await request.formData();
        // const user = Object.fromEntries(formData);
        // authService.loginUser(user.email, user.password);
        const { email, password } = userLogins;
        if (!!email && !!password) {
            // const { from } = location.state || { from: { pathname: '/' }};
            authService.loginUser(email, password)
                // .then(() => navigate(from, { replace: true }))
                .catch(() => {
                    // setError(true);
                    setUserLogins({ email: '', password: '' });
                })
        }
    }

    return(
    <>
        <form 
            className={s.loginBody}
            onSubmit={loginUser}
        >
            <h3>Login</h3>
            <p>Enter your email and password</p>
                <input 
                    className={s.inputBase} 
                    name="email" 
                    type="text" 
                    placeholder="Email"
                    autoComplete="off"
                    onChange={onChange}
                />
                <input 
                    className={s.inputBase} 
                    name="password" 
                    type="password" 
                    placeholder="Password"
                    autoComplete="off"
                    onChange={onChange}
                />
            <input 
                className={s.submitBtn} 
                type="submit" 
                value="Login" 
            />
        </form>
        <div className={s.link}>
            No account? Create one <Link to='/register'>HERE</Link> 
            <br /> OR <br />
            <Link to='/'>Return Home</Link>
        </div>
    </>
    )
}

export default LoginUser;
