import axios from "axios";

// local
// const BASE_URL = 'http://localhost:3001/api/v1/';
// url of other host
const BASE_URL = 'http://localhost:5001/api/v1/peaks';

// let mountains = [];

export const getAllMountains = async () => {
    try {
        let response = await axios.get(BASE_URL);
        response = response.data.data.map((peak) => ({
            id: peak._id,
            name: peak.name,
            elevation: peak.elevation,
            forest: peak.forest,
            range: peak.range,
            rank: peak.rank,
            photo: peak.photo,
            numberOfRoutes: peak.routes.length,
            routes: peak.routes
        }));
        // mountains = response;
        console.log(response);
        // return response;
    } catch (err) {
        console.error(err);
    }
}
