import React, { useEffect, useState } from "react";
import Login from "../Login/Login";
import MountainContainer from "../PeakContainer/PeakContainer";
import s from "./FourteenersApp.module.css";
import { getAllPeaks } from "../../services";
import Navbar from "../Navbar/Navbar";
import { useLoaderData } from "react-router-dom";


// use loaders here to fetch data and pass it to index.js as loader and manipulate data like react router
export async function loader({ request }) {
    const peaks = await getAllPeaks();
    return { peaks }
}

const FourteenersApp = () => {

    const peakData = useLoaderData();

    const [showLogin, setShowLogin] = useState(false);
    // const [peakData, setPeakData] = useState([]);



    // useEffect(() => {
    //     getAllMountains().then((res) => setPeakData(res));
    // }, [])

    return (
        <div className={s.app}>
            <Navbar 
                toggle={() => setShowLogin(!showLogin)}
            />
            {  showLogin
                ? <Login />
                : <MountainContainer
                    peakData={peakData}
                 />
            }
        </div>
    )
}

export default FourteenersApp;
