import React, { useEffect, useState } from "react";
import s from "./PeakDetails.module.css";
import { useParams } from "react-router-dom";
import { getPeakData } from "../../services";
import ErrorPage from "../ErrorPage/ErrorPage";

const PeakDetails = () => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(false);
    const [peakData, setPeakData] = useState();
    let peakId = useParams();

    const createImgObject = (url) => {
        console.log('url', url);
    }

    useEffect(() => {
        setLoading(true);
        const { id } = peakId;
        getPeakData(id)
            .then((res) => {
                setPeakData(res);
                setLoading(false);
                createImgObject(peakData.photo);
            })
            .catch(() => {
                setLoading(false);
                setError(true);
            })
    }, []);

    return (
        <div className={s.detailsBody}>
            {error && <ErrorPage />}
            {loading && <div>Loading...</div>}
            Peak Detail Page
            {
                peakData && 
                <div 
                    className={s.peakDetails}

                >
                    <h3>{peakData.name}</h3>
                    <div 
                        className={s.imgContianer}
                        // style={{ backgroundImage: `url("${peakData.photo})`}}
                    >

                    </div>


                </div>
            }
        </div>
    )
}

export default PeakDetails;
