import axios from "axios";

// local
// const BASE_URL = 'http://localhost:3001/api/v1/';
// url of other host
const BASE_URL = 'http://localhost:5001/api/v1/';
const PEAKS_URL = BASE_URL + 'peaks';
const LOGIN_URL = BASE_URL + 'auth/login';

// let mountains = [];

export const getAllPeaks = async () => {
    try {
        let response = await axios.get(PEAKS_URL);
        response = response.data.data.map((peak) => ({
            id: peak._id,
            name: peak.name,
            elevation: peak.elevation,
            forest: peak.forest,
            range: peak.range,
            rank: peak.rank,
            photo: peak.photo,
            numberOfRoutes: peak.routes.length,
            routes: peak.routes
        }));
        return response;
    } catch (err) {
        console.error(err);
    };
};

export const loginUser = async (email, password) => {
    const body = { "email": email.toLowerCase(), "password": password };
    try {
        const response = axios.post(LOGIN_URL, body, {
            'Content-Type': 'application/json'
        });
        console.log('login', response);
    } catch (error) {
        console.error(error)
    }
}
